fn run_on_sample() {
    let mut sample_input = Vec::new();
    sample_input.push(("(())", 0));
    sample_input.push(("()()", 0));
    sample_input.push(("(((", 3));
    sample_input.push(("(()(()(", 3));
    sample_input.push(("))(((((", 3));
    sample_input.push(("())", -1));
    sample_input.push(("))(", -1));
    sample_input.push((")))", -3));
    sample_input.push((")())())", -3));

    for input in &sample_input {
        let result = find_floor(input.0);
        if result == input.1 {
            println!("✅ result={}; expected={}", result, input.1)
        } else {
            println!("❌ result={}; expected={}", result, input.1)
        }
    }
}

